package com.example.kotlin22.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;


import com.example.kotlin22.GameModel;
import com.example.kotlin22.ItemClickListener;
import com.example.kotlin22.R;
import com.example.kotlin22.adapter.LevelAdapter;
import com.example.kotlin22.databinding.FragmentLevelChooseBinding;

import java.util.ArrayList;

public class LevelChooseFragment extends Fragment implements ItemClickListener {

    private FragmentLevelChooseBinding binding;
    private LevelAdapter adapter;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentLevelChooseBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        initAdapter();
    }

    private void initAdapter() {
        ArrayList<GameModel> list = new ArrayList<>();
        list.add(new GameModel("https://bigenc.ru/media/2016/10/27/1235192535/15631.jpg",
                "https://firadent.com/files/img/statiya/vidi-keramicheskih-koronok/s2.jpg",
                "https://mkplitka.ru/assets/images/nefrit-keramika/ferns/678678_1560420915.jpg",
                "data:image/jpeg;base64,",
                "Керамика", "1", "1 уровень"));
        list.add(new GameModel("data:image/jpeg;base64,",
                "data:image/jpeg;base64,",
                "data:image/jpeg;base64,",
                "data:image/jpeg;base64,",
                "Вода", "2", "2 уровень"));
        adapter = new LevelAdapter(list, this);
        binding.recyclerView.setAdapter(adapter);
    }


    @Override
    public void itemClick(GameModel model) {
        Bundle bundle = new Bundle();
        bundle.putSerializable("game", model);
        Navigation.findNavController(requireView()).navigate(R.id.gameFragment, bundle);
    }
}